function check_colors() {
    var e = localStorage.getItem("primary_color") ? localStorage.getItem("primary_color") : "#000000";
	var t = localStorage.getItem("secondary_color") ? localStorage.getItem("secondary_color") : "#222222";
	var o = localStorage.getItem("brand_color") ? localStorage.getItem("brand_color") : "#000000";
	var r = localStorage.getItem("brand_color_70") ? localStorage.getItem("brand_color_70") : "#00000082";
	var c = localStorage.getItem("brand_color_30") ? localStorage.getItem("brand_color_30") : "#00000047";

    document.documentElement.style.setProperty("--primary", e);
    document.documentElement.style.setProperty("--secondary", t);
    document.documentElement.style.setProperty("--brand", o);
    document.documentElement.style.setProperty("--brand-70", r);
    document.documentElement.style.setProperty("--brand-30", c);
}

check_colors();